import PortfolioItem from "../portfolioItem/PortfolioItem"
import { useState } from "react";
import "./portfolio.scss"
import { Featured, Projects, Hobbies, React } from "../PortfolioData";

export default function Portfolio() {
    const list =[
        {
            id:"1",
            value:"Featured"
        },
        {
            id:"2",
            value:"Projects"
        },
        {
            id:"3",
            value:"Hobbies"
        },
        {
            id:"4",
            value:"React"
        }
    ]

    const [state, setstate] = useState("Featured");
    
    function handleChange(value){
      setstate(value)
    }
    return (
        <div className="portfolio" id="portfolio">
            <div className="wrapper">
                <h1>Portfolio</h1>
                <ul>
                {list.map((item) => {
                    return(
                    <PortfolioItem 
                     key={item.id} 
                     item={item.value} 
                     active={state === item.value} 
                     handleClick={handleChange}/>
                )})}
                
                   
                </ul>
                <div className="imgContainer">
                  <div className="item">
                    <img src="" alt=""></img>
                    <h3>website</h3>
                  </div>
                  <div className="item">
                    <img src="images/website.jpg" alt=""></img>
                    <h3>website</h3>
                  </div>
                  <div className="item">
                    <img src="images/website.jpg" alt=""></img>
                    <h3>website</h3>
                  </div>
                  <div className="item">
                    <img src="images/website.jpg" alt=""></img>
                    <h3>website</h3>
                  </div>
                  <div className="item">
                    <img src="images/website.jpg" alt=""></img>
                    <h3>website</h3>
                  </div>
                  <div className="item">
                    <img src="images/website.jpg" alt=""></img>
                    <h3>website</h3>
                  </div>
                  
                </div>

            </div>
        </div>
    )
}
